mapper.select:
SELECT C.* FROM( SELECT ICI.CLIENT_NO AS CLIENT_NO , ICI.CLIENT_NAME AS CLIENT_NAME , ICI.BIRTHD AS BIRTHD, ICI.GENDER AS GENDER ,ICI.PH_NO AS PH_NO , ICI.PH_DR AS PH_DR , ICI.EMAIL AS EMAIL , ICI.EMAIL_DR AS EMAIL_DR ,ICI.ADDRESS AS ADDRESS ,JI.JOIN_NO AS JOIN_NO ,JI.JDATE AS JDATE , JI.JOIN_EDATE AS EDATE ,PI.PAY_NO AS PAY_NO , PI.TARGET_DATE AS TARGET_DATE , PI.PAY_MONEY AS PAY_MONEY , PI.PAY_WHETHER AS PAY_WHETHER , ROW_NUMBER()OVER(ORDER BY ICI.CLIENT_NO) AS RNUM
FROM INSIDE_CLIENT_INFO ICI INNER JOIN JOIN_INFO JI ON ICI.CLIENT_NO = JI.CLIENT_NO INNER JOIN PAY_INFO PI ON JI.JOIN_NO = PI.JOIN_NO WHERE 1 = 1
${str} ) C WHERE C.RNUM BETWEEN #{startCnt} AND #{endCnt}

SELECT B.* FROM( SELECT CMP_NO, CMP_NAME, TO_CHAR(SDATE,'YYYY-MM-DD') AS SDATE, TO_CHAR(EDATE,'YYYY-MM-DD') AS EDATE, PROG_STAT, EMP_NAME, CMP.ADD_DAY, ROW_NUMBER() OVER(ORDER BY ADD_DAY DESC) AS RNUM FROM CMP INNER JOIN EMP_INFO ON CMP.MGR_NO = EMP_INFO.EMP_NO WHERE 1 = 1
AND CMP_NAME LIKE '%' || #{textbox_srch_txt} || '%'
AND PROG_STAT LIKE '%' || #{textbox_srch_txt} || '%'
AND EMP_NAME LIKE '%' || #{textbox_srch_txt} || '%'
)B WHERE B.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE CMP SET PROG_STAT=(SELECT PROG_STAT
							FROM CMP
							WHERE CMP_NO = #{seq}
							)	 +	1
WHERE CMP_NO = #{seq}

mapper.select:
SELECT *
FROM CMP_CHANNEL CC INNER JOIN CHANNEL_COMP CP ON CC.CHANNEL_COMP_NO = CP.CHANNEL_COMP_NO
WHERE CMP_NO = #{seq}
