mapper.select:
SELECT COUNT(*) FROM(SELECT C.CLIENT_NO , C.CLIENT_NAME , C2.COMP_NAME, C2.COMP_NO ,C2.COMP_ZC, C2.COMP_ADDR, C2.COMP_DETAIL_ADDR FROM CLIENT C INNER JOIN COMP C2 ON C.COMP_NO = C2.COMP_NO
AND (CLIENT_NAME LIKE'%' || #{searchTxt} || '%' OR COMP_NAME LIKE'%' || #{searchTxt} || '%')
) A

SELECT COUNT(*) FROM(SELECT LD.LEAD_NO, LD.LEAD_NAME, EI.EMP_NAME FROM LEAD LD INNER JOIN EMP_INFO EI ON LD.EMP_NO = EI.EMP_NO
AND (LEAD_NAME LIKE'%' || #{searchTxt} || '%' OR EMP_NAME LIKE'%' || #{searchTxt} || '%')
) A

SELECT COUNT(*) FROM(SELECT CHN_NO,CHN_NAME,EMP_NAME FROM BSS_CHN BC INNER JOIN EMP_INFO EI ON BC.EMP_NO = EI.EMP_NO
AND (CHN_NAME LIKE'%' || #{searchTxt} || '%' OR EMP_NAME LIKE'%' || #{searchTxt} || '%')
) A

SELECT COUNT(*)
FROM SCHE_ATTACH 
WHERE SCHE_NO = #{sche_no}

SELECT COUNT(*)
FROM SCHE_PROD
WHERE SCHE_NO = #{sche_no}

SELECT PROG_NO
FROM BSS_CHN
WHERE CHN_NO = #{cNo}

mapper.update:
UPDATE SCHE_LEAD SET 
		LEAD_NO = #{cNo}
WHERE SCHE_NO = #{sche_no}

UPDATE SCHE_CHN SET 
		CHN_NO = #{cNo},
		PROG_NO = #{progCnt}
WHERE SCHE_NO = #{sche_no}

mapper.select:
SELECT ML_NAME, ML_PARTICIPANT, ML_CON
FROM ML 
WHERE ML_NO =  #{minutes_no}

SELECT SCHE_NO, SDATE, EDATE, SCHE, SCHE_DIV_NO, TASKDIV, COMP_NAME, CLIENT_NAME FROM ( SELECT SC.SCHE_NO, TO_CHAR(SC.SCHE_SDATE, 'YY-MM-DD HH24:MI:SS') AS SDATE, TO_CHAR(SC.SCHE_EDATE, 'YY-MM-DD HH24:MI:SS') AS EDATE, C1.CODE_NAME AS SCHE, C1.CODE_S_CATE AS SCHE_DIV_NO, C2.CODE_NAME AS TASKDIV, NVL(COMP_NAME, '-') AS COMP_NAME, NVL(CLIENT_NAME, '-') AS CLIENT_NAME, ROW_NUMBER() OVER(ORDER BY SC.SCHE_NO DESC) AS RNUM FROM SCHE SC LEFT OUTER JOIN SCHE_CLIENT_INFO CI ON SC.SCHE_NO = CI.SCHE_NO LEFT OUTER JOIN CLIENT CL ON CI.CLIENT_NO = CL.CLIENT_NO LEFT OUTER JOIN COMP CO ON CL.COMP_NO = CO.COMP_NO INNER JOIN COMMON_CODE C1 ON SC.SCHE_DIV_NO = C1.CODE_S_CATE AND C1.CODE_L_CATE = 2 INNER JOIN COMMON_CODE C2 ON SC.SCHE_ACTI_NO = C2.CODE_S_CATE AND C2.CODE_L_CATE = 3 WHERE SC.DEL_YN = 1
AND TO_CHAR(SC.SCHE_SDATE, 'YYYYMMDD') <= #{date} AND TO_CHAR(SC.SCHE_EDATE, 'YYYYMMDD') >= #{date}
AND
COMP_NAME LIKE '%' || #{srchText} || '%'
CLIENT_NAME LIKE '%' || #{srchText} || '%'
(COMP_NAME LIKE '%' || #{srchText} || '%' OR CLIENT_NAME LIKE '%' || #{srchText} || '%')
) WHERE RNUM BETWEEN #{startCnt} AND #{endCnt}
